INSERT INTO `mdl_book` (`id`, `course`, `name`, `intro`, `introformat`, `numbering`, `navstyle`, `customtitles`, `revision`, `timecreated`, `timemodified`) VALUES
(1, 1, 'Hướng dẫn thiết kế khóa học dành cho giảng viên Các yêu cầu hoàn thành', '<p dir=\"ltr\" style=\"text-align: left;\"></p><h3></h3><div><p></p><h3></h3><p></p><span style=\"font-size: 0.9375rem;\">&nbsp;* Quy trình mở khóa học</span><br><ul><li>Giảng viên cung cấp thông tin khóa học cho BCT E-Learning gồm các thông tin:</li><li>Mã học phần;</li><li>Tên học phần</li><li>Email của GV (Email Office 365 do Trường cấp dạng …@my.tvu.edu.vn)</li><li>BCT sẽ tạo khóa học giống khóa học mẫu và thông báo cho GV yêu cầu.</li></ul></div><br><p></p>', 1, 1, 1, 0, 8, 1732956610, 1732957514),
(2, 1, 'Tài liệu hướng dẫn sử dụng Zotero quản lý trích dẫn trong viết báo cáo Completion requirements', '<p dir=\"ltr\" style=\"text-align: left;\"></p><h3>1. LỜI NÓI ĐẦU</h3><div><p><img src=\"https://lms.tvu.edu.vn/pluginfile.php/684660/mod_book/chapter/520/z4812351798810_5a568d8212cfba12ae799097daac9198.jpg\" width=\"300\" height=\"391\" alt=\"zotero\">Việc quản lý tài liệu tham khảo (TLTK) và trích dẫn là rất quan trọng trong việc viết một bài báo, luận văn, sách, hoặc bất kỳ dạng văn bản học thuật nào khác, việc đưa ra các nguồn tham khảo đáng tin cậy và chính xác để hỗ trợ quan điểm và thông tin của bạn là cần thiết. Nó không chỉ tôn trọng quyền tác giả, mà còn tạo ra tính minh bạch trong nghiên cứu, cho phép người khác kiểm tra và theo dõi nguồn gốc của thông tin.</p><p>Zotero là một phần mềm trợ giúp quản lý TLTK miễn phí, giúp người dùng thu thập, tổ chức, trích dẫn và chia sẻ tài liệu nghiên cứu. Zotero được thiết kế để hỗ trợ nhiều nguồn thông tin khác nhau, từ sách, bài báo, video, web và nhiều hơn nữa. Tính năng tự động trích xuất thông tin từ nguồn web của nó giúp tiết kiệm thời gian và giảm sai sót khi ghi chú và trích dẫn. Nó cung cấp hỗ trợ cho nhiều phong cách trích dẫn khác nhau, từ APA đến Chicago, cho phép người dùng tạo các bộ sưu tập TLTK riêng hoặc chia sẻ với người khác. Zotero còn tích hợp với các phần mềm xử lý văn bản như Microsoft Word và Google Docs, giúp tạo ra các trích dẫn và danh sách tham khảo một cách dễ dàng và nhanh chóng. Zotero là một công cụ đắc lực cho bất kỳ ai đang tiến hành nghiên cứu học thuật.</p><p><strong>Các hướng dẫn được trình bày trong tài liệu này là:</strong></p><p>Cài đặt Zotero vào máy tính.</p><p>Tạo tài khoản Zotero.</p><p>Quản lý bộ sưu tập TLTK.</p><p>Tạo ghi chú và gắn thẻ cho TLTK</p><p>Tạo trích dẫn và danh sách tham khảo.</p><p>Chia sẻ và cộng tác sử dụng bộ sưu tập TLTK.</p></div><br><p></p>', 1, 1, 1, 0, 1, 1732956638, 1732956638);
